def add_coords(df, lats, lons, region):
    """
    Add lat, lon points to csv so we can use this for plotting later on
    """
    if region == 'north_america':
        points = 1519
    if region == 'europe':
        points = 837

    total_entries = len(df)
    list_multiplier = int(total_entries/points)
    lat_list = lats*list_multiplier
    lon_list = lons*list_multiplier
    df['lon'] = lon_list
    df['lat'] = lat_list

    return df


def combine_data(data_dir, split, month, geo):
    """
    Combine momo and gee data
    """
    month_dict = {'June': 30, 'July': 31, 'Aug': 31}
    # Load in momo features
    if split == 'train':
        year_range = '2005-2015'
        years = range(2005, 2016)
    else:
        year_range = '2016'
        years = [2016]

    momo_df = pd.read_csv('{}/28features_{}_{}_{}_features.csv'.format(data_dir,geo, month, year_range))
    momo_df = momo_df.drop(['Unnamed: 0'], axis=1, errors='ignore').reset_index(drop=True)

    # Load modis
    modis_df_list = []
    for y in years:
        df = pd.read_csv('{}/{}_{}_modis_features.csv'.format(data_dir, y, geo))
        df_month = pd.concat([df] * month_dict[month], ignore_index=True)
        modis_df_list.append(df_month)
        # multiply by the number of days before appending above
    modis_features = pd.concat(modis_df_list)
    modis_features = modis_features.drop(['Unnamed: 0'], axis=1, errors='ignore').reset_index(drop=True)

    # Load pop
    pop_df_list = []
    for y in years:
        df = pd.read_csv('{}/{}_{}_population_features.csv'.format(data_dir, y, geo))
        df_month = pd.concat([df] * month_dict[month], ignore_index=True)
        pop_df_list.append(df_month)
    pop_features = pd.concat(pop_df_list)
    pop_features = pop_features.drop(['Unnamed: 0'], axis=1, errors='ignore').reset_index(drop=True)

    combined = pd.concat([momo_df, modis_features, pop_features], axis=1)
    return combined


if __name__ == '__main__':
    args = get_args()
    split = args.split
    region = args.region
    month = args.month
    data_dir = args.data_dir
    save_dir = args.save_dir

    if region == 'north_america':
        lats = na_lats
        lons = na_lons
    if region == 'europe':
        lats = eu_lats
        lons = eu_lons

    # Combine momo and gee features
    combined_df = combine_data(data_dir, split, month, region)

    # Add latitude and longitude back in
    combined_df_with_loc = add_coords(combined_df, lats, lons, region)

    if split == 'train':
        save_name = '2005-2015_train_{}_{}_momo_gee_features.csv'.format(region, month)
    if split == 'test':
        save_name = '2016_test_{}_{}_momo_gee_features.csv'.format(region, month)

    combined_df_with_loc.to_csv('{}/{}'.format(save_dir, save_name), index=False)

