def generate_feature_df(sample_directory, label_directory, region, save_directory):
    """
    Generate dataframe of features from the numpy array
    """
    df_list = []
    for f in os.listdir(sample_directory):
        df = pd.DataFrame()
        if f.endswith('.npy'):
            arr = np.load(os.path.join(sample_directory, f))
            date = f.split('_sample.npy')[0]
            label = np.load(os.path.join(label_directory, '{}_label.npy'.format(date)))
            for i in range(len(var_list_51)):
                arr_flat = arr[i,:,:].flatten()
                df['{}'.format(var_list_51[i])] = arr_flat
                df['Year'] = date[0:4]
                df['Month'] = date[5:7]
                df['Day'] = date[-2:]
            label_flat = label.flatten()
            df['bias'] = label_flat
            if region == 'NorthAmerica':
                df['lat'] = na_lats
                df['lon'] = na_lons
            if region == 'Europe':
                df['lat'] = eu_lats
                df['lon'] = eu_lons
        df_list.append(df)

    total_df = pd.concat(df_list)
    total_df = total_df.dropna()
    total_df = total_df.reset_index(drop=True)
    return total_df


